import { useRef, useEffect, useState } from "react";

import './App.css'
import Unit from './components/Unit/Unit.js'
import TimeColumn from './components/TimeColumn/TimeColumn.js'
import {getBinary} from './utils/getBinary.js'


function App() {

  const [time, setTime] = useState(new Date());
  const binary_time = getBinary(time);

  useEffect(() => {
    const interval = setInterval(() => {
      setTime(new Date());
    }, 1000);

    return () => clearInterval(interval);
  }, []);





  return (
    <div className="App">
      <h1>My binary clock</h1>

      <Unit />
      <TimeColumn numUnits={3}/>

      <div>{time.toLocaleTimeString()}</div>
      <div>{binary_time}</div>

      <div className="clock-outer-container">
        {/* Hour tens */}
        <TimeColumn numUnits={2} binary={binary_time.get("hour_tens")} />

        {/* Hour units */}
        <TimeColumn numUnits={4} binary={binary_time.get("hour_units")} />

        {/* Minute tens */}
        <TimeColumn numUnits={3} binary={binary_time.get("minute_tens")} />

        {/* Minute units */}
        <TimeColumn numUnits={4} binary={binary_time.get("minute_units")} />

        {/* Second tens */}
        <TimeColumn numUnits={3} binary={binary_time.get("second_tens")} />

        {/* Second units */}
        <TimeColumn numUnits={4} binary={binary_time.get("second_units")} />
      </div>

    </div>
  );
}

export default App;
