import { useRef, useEffect } from "react";

function CanvasDrawing() {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    // ðŸ”² Rectangle
    ctx.fillStyle = "blue";
    ctx.fillRect(50, 50, 100, 80);

    // âš« Circle
    ctx.beginPath();
    ctx.arc(200, 100, 40, 0, 2 * Math.PI);
    ctx.fillStyle = "red";
    ctx.fill();

    // ðŸ”º Triangle
    ctx.beginPath();
    ctx.moveTo(100, 200);
    ctx.lineTo(150, 300);
    ctx.lineTo(50, 300);
    ctx.closePath();
    ctx.fillStyle = "green";
    ctx.fill();

    // âž– Line
    ctx.beginPath();
    ctx.moveTo(20, 20);
    ctx.lineTo(200, 20);
    ctx.strokeStyle = "black";
    ctx.lineWidth = 2;
    ctx.stroke();
  }, []);

  return (
    <canvas
      ref={canvasRef}
      width={400}
      height={400}
      style={{ border: "1px solid black" }}
    />
  );
}

export default CanvasDrawing;
