import { useRef, useEffect } from "react";

import './App.css'
import Unit from './components/Unit/Unit.js'
import TimeColumn from './components/TimeColumn/TimeColumn.js'


function App() {


  useEffect(() => {
    const interval = setInterval(() => {
      setTimeout(new Date());
    }, 1000);

    return () => clearInterval(interval);
  }, []);


  return (
    <div className="App">
      <h1>My binary clock</h1>

      <Unit />
      <TimeColumn numUnits={3}/>

      {/* Hour tens */}
      <div className="clock-outer-container">
        {/* Hour tens */}
        <TimeColumn numUnits={2}/>

        {/* Hour units */}
        <TimeColumn numUnits={4}/>

        {/* Minute tens */}
        <TimeColumn numUnits={3}/>

        {/* Minute units */}
        <TimeColumn numUnits={4}/>

        {/* Second tens */}
        <TimeColumn numUnits={3}/>

        {/* Second units */}
        <TimeColumn numUnits={4}/>
      </div>

    </div>
  );
}

export default App;
